        /// <summary>
        /// MessageBoxの表示内容を言語ファイルから取得する処理
        /// </summary>
        /// <param name="MessageKey">名前</param>
        /// <param name="FormName">親Formの名前</param>
        /// <param name="LanguageFile">言語ファイル</param>
        /// <returns>表示内容</returns>
        public static string GetMessageBoxMessage(string MessageKey, string FormName, XElement LanguageFile)
        {
            return SearchFromLanguageFile(MessageKey, FormName, LanguageFile, "MessageBox");
        }

