        /// <summary>
        /// 言語ファイルから表示内容を検索
        /// </summary>
        /// <param name="MessageKey">名前</param>
        /// <param name="FormName">親フォームの名前</param>
        /// <param name="LanguageFile">言語ファイル</param>
        /// <param name="targetElements">検索対象の種類</param>
        /// <returns>表示内容</returns>
        private static string SearchFromLanguageFile(string MessageKey, string FormName, XElement LanguageFile, string targetElements)
        {
            try
            {
                IEnumerable<XElement> ItemDataList = from item in LanguageFile.Elements(FormName).Elements(targetElements).Elements("item") select item;
                foreach (XElement itemData in ItemDataList)
                {
                    try
                    {
                        if (itemData.Element("itemname").Value == MessageKey)
                        {
                            return itemData.Element("itemtext").Value;
                        }
                    }
                    catch (Exception ex)
                    {
                        return ex.Message;
                    }
                }
            }
            catch (Exception ex)
            {
                return ex.Message;
            }
            return "言語ファイルが破損しています。\nLanguage File is BROKEN.";
        }

